import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';
import { useEffect,useState } from 'react';


const AvailableMeals = () => {
  const [meals,setMeals] = useState([])
  const [isLoading,setIsLoading] = useState(false)
  const [httpError,setHttpError] = useState()

  useEffect(()=> {
    //harus gini soalnya gabisa kalo async di useEffect
    //kalo ngambil data dari db harusnya di useeffect terus
    try{const fetchMeals = async () => {
      setIsLoading(true)
      const DUMMY_MEALS = await fetch('https://practice-841a5-default-rtdb.firebaseio.com/meals.json')

      if(!DUMMY_MEALS.ok){
        setHttpError
      }
      const data = await DUMMY_MEALS.json()      

      const mealsList = [];

      for (const key in data){
        mealsList.push({
          id:key,
          key:key,
          name:data[key].name,
          description:data[key].description,
          price:data[key].price,
        })
      }
      setMeals(mealsList)
      setIsLoading(false)
    }} catch (error){

    }
    fetchMeals();
  },[]) //only run when the components run first time

  //map itu buat array
  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      {isLoading&&<p className={classes.MealsLoading}>Is Loading....</p>}
      {!isLoading&&<Card>
        <ul>{mealsList}</ul>
      </Card>}
    </section>
  );
};

export default AvailableMeals;
