import { useContext,useState } from 'react';

import Modal from '../UI/Modal';
import CartItem from './CartItem';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import Checkout from './Checkout'
import React from 'react';

const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  const [orderClicked,setOrderClicked] = useState(false)
  const [isSubmitting,setIsSubmitting] = useState(false)
  const [didSubmit,setDidSubmit] = useState(false)


  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem(item);
  };

  const orderHandler = ()=>{
    setOrderClicked(true)
  }

  const submitOrderHandler = async(userData) => {
    setIsSubmitting(true)
    const response = await fetch('https://practice-841a5-default-rtdb.firebaseio.com/orders.json',{
      method:'POST',
      body:JSON.stringify({
        user:userData,
        orderedItems:cartCtx.items
      })
    })
    setIsSubmitting(false);
    setDidSubmit(true)
    cartCtx.clearItem();
  }

  const cartItems = (
    <ul className={classes['cart-items']}>
      {/* dari semua items yg ada di cart, di map buat ditampilin */}
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  const modalActions = <div className={classes.actions}>
  <button className={classes.button} onClick={props.onClose}>
    Close
  </button>
  {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}
  </div>

  const cartModalContent = <React.Fragment>
    {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {orderClicked&&<Checkout onAdd={submitOrderHandler} onClose={props.onClose}/>}
      {!orderClicked&&modalActions}
  </React.Fragment> 

  const isSubmittingData = <p>is Submitting Data...</p>

  const didSubmitData = <React.Fragment>
    <p>Order Submitted</p>
    <div className={classes.actions}>
  <button className={classes['button--alt']} onClick={props.onClose}>
    Close
  </button>
  </div>
  </React.Fragment>

  return (
    <Modal onClose={props.onClose}>
      {!isSubmitting && !didSubmit && cartModalContent}
      {isSubmitting&&isSubmittingData}
      {!isSubmitting&&didSubmit&&didSubmitData}
    </Modal>
  );
};

export default Cart;
